mapper.select:
SELECT e.*, d.name AS deptName
FROM emp e
LEFT JOIN dept d ON e.dept_id = d.id


        AND e.name LIKE CONCAT('%',#{dto.name},'%')


        AND e.gender = #{dto.gender}


        AND e.entry_date >= #{dto.begin}


        AND e.entry_date  <=  #{dto.end}


ORDER BY e.update_time DESC
